package javasessions;

public class FinalConcept {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	 
		//Variables declared with final keyword can't be changed
		//Used to avoid inheritence
		final int i =20;
		//i=30;
		System.out.println(i); 	// TO DEFINE THE CONSTANT VALUES
		final int x= 30;
	}

}
