package javasessions;

public class PowerConcept {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int base=2;
		int exp=10;
		long result =1;
		while(exp!=0) {
			result=result*base;
			exp--;
		}
		System.out.println("Power of number is"+" "+result);
		System.out.println(Math.pow(2,05));
	}

}
